{% renderpartial 'default' %}

{% paginate posts by 3 %}

<div class="row">
    {% for post in posts %}
    <div class="col-md-4">
        <p>{{ post.datePublished | date: "%Y-%m-%d %H:%M" }}</p>
        {% if post.imageUrl %}
        <a href="{{ post.fullPath }}">
            <img src="{{ post.mainImage.url }}" class="img-fluid" />
        </a>
        {% endif %}
        <h3><a href="{{ post.fullPath }}">{{ post.title }}</a></h3>
        <p>{{ post.excerpt }}</p>
        {{ post.tags | default_tagging }}
    </div>
    {% else %}
    <p>There are no posts yet.</p>
    {% endfor %}
</div>

<div class="row md-top-5">
    {{ paginate | default_pagination }}
</div>

{% endpaginate %}

{% layout 'BlogMain' %} 
