								<ul class="c-tags c-theme-ul-bg">
									{% for tag in post.tags %}
										{% assign tag_param = tag | url_encode | prepend: '?tag='  %}
										{% assign url = site.fullPath | append: tag_param %}			
										{% if tag == currentTag %}
											{% assign tagClass = "krh-current-tag" %} 
										{% else %}
											{% assign tagClass = "" %} 
										{% endif %}										
										<li class="{{ tagClass }}"><a href="{{ url }}">{{ tag }}</a></li> 
										<!-- <li><span>{{ tag }}</span></li> -->
									{% endfor %}
								</ul>
                            </div>
