								{% if paginate.previous %}
									<li class="c-prev"><a href="{{ paginate.previous.url }}"><i class="fa fa-angle-left"></i></a></li>
								{% endif %}

								{% for part in paginate.parts %}
									{% if part.num == paginate.currentPage %}
										<li class="c-active"><a href="{{ part.url }}">{{ part.num }}</a></li>
									{% else %}
										<li><a href="{{part.url}}">{{ part.num }}</a></li>
									{% endif %}
								{% endfor %}

								{% if paginate.next %}
									<li class="c-next"><a href="{{ paginate.next.url }}"><i class="fa fa-angle-right"></i></a></li>
								{% endif %}
							</ul>
						</div>
					{% endif %}
