{% layout 'BlogMain' %} 
{% assign pageTitle=page.title %}

<div class="c-content-box c-size-sm c-overflow-hide c-bg-white">
	<div class="container krh-devblog">
        <div class="c-layout-breadcrumbs-1 c-fonts-uppercase c-fonts-bold c-bordered c-bordered-both">
            <div class="container" style="margin-left:0;">
                <ul class="c-page-breadcrumbs c-theme-nav c-pull-left c-fonts-regular">
                    <li>
                        <a href="{{ site.path }}">{{ site.name }}</a>
                    </li>
                    <li>/</li>
                    <li class="c-state_active">{{ page.title }}</li>
                </ul>
                <div class="a2a_kit a2a_kit_size_32 a2a_default_style" style="float:right; margin-top:4px">
        			<a class="a2a_button_facebook"></a>
        			<a class="a2a_button_twitter"></a>
        			<a class="a2a_button_google_plus"></a>
        			<a class="a2a_button_linkedin"></a>
    		    </div>
            </div>

        </div>

		<div class="row">
			<div class="col-lg-8">
				<div class="c-content-blog-post-1">
                    <div class="c-content-title-1 c-margin-b-20">
						<h1 style="margin-bottom:0;">{{ page.title }}</h1>
					</div>
					
					{% if page.mainImage.url %}
						<div class="c-margin-b-50">
							<img src="{{ page.mainImage.url }}" class="img-fluid" alt="{{ page.mainImage.alt }}" title="{{ page.mainImage.title }}" />
							{% if page.mainImage.caption %}
								<div class="krh-image-caption">{{ page.mainImage.caption }}</div>
							{% endif %}
						</div>
					{% endif %}

					
					<div class="c-panel">
						<div class="c-date">{{ page.datePublished | date: "%Y-%m-%d" }}</div>
						<ul class="c-tags c-theme-ul-bg">
							{% for tag in page.tags %}
								{% assign tag_param = tag | url_encode | prepend: '?tag='  %}
								{% assign url = site.path | append: tag_param %}			
								<li><a href="{{ url }}">{{ tag }}</a></li> 
							{% endfor %}
						</ul>
					</div>
					{% renderbody %}
				</div>

				<nav class="c-margin-t-80">
					<ul class="pager">
						{% if page.prevUrl %}
							<li class="previous"><a href="{{page.prevUrl}}"><span aria-hidden="true">←</span>&nbsp;Older</a></li>
						{% endif %}
						{% if page.nextUrl %}
							<li class="next"><a href="{{page.nextUrl}}">Newer&nbsp;<span aria-hidden="true">→</span></a></li>
						{% endif %}
					</ul>
				</nav>
				
				<!------ Disqus block start -----> 
				<div id="disqus_thread" class="c-margin-t-80">
				</div>
				<noscript>Please enable JavaScript to view the <a href="https://disqus.com/?ref_noscript">comments powered by Disqus.</a></noscript>
				<!------ Disqus block end -----> 
			</div>
			<div class="col-lg-4">
                <div class="c-margin-b-50">
                    {% renderpartial 'BlogRecentPostsPartial' %}
                </div>

                <div class="krh-blog-sticky">
    			    <div class="subscr-block">
        				{% renderpartial 'BlogSubscribe' %}
    			    </div>
    
                    <div style="display: none;">
        				<div class="c-content-title-1 c-theme c-title-md c-margin-t-50">
        					<h3 class="c-font-bold c-font-uppercase">Supported by EasyQuery</h3>
        					<div class="c-line-left c-theme-bg"></div>
        				</div>
        				<div class="c-margin-b-40">
        					<a href="/easyquery">EasyQuery</a> is a multi-platform solution that lets you embedding ad-hoc reporting or advanced search functionality to your application
        				</div>
        				<div class="c-tile-content c-arrow-right c-arrow-green c-content-overlay">
        					<a href="//korzh.com/easyquery">
        						<div class="c-overlay-wrapper" style="width: 300px;">
        							<div class="c-overlay-content">
        								<i class="icon-link"></i>
        							</div>
        						</div>
        					</a>
        					<div class="c-image c-overlay-object" data-height="height" style="background-image: url(&quot;//cdn.korzh.com/images/eq_demo_box.gif&quot;); height: 251px; width: 300px;"></div>
        				</div>
                    </div>
                    
                    <div class="c-margin-t-40">
                        {% renderpartial 'BlogAdsPartial' %}
                    </div>
                    
                </div>
			</div>
		</div>
	</div>
</div>

{% section styles %}

    <style>
        @@media (min-width: 1600px) {
            .container {
                width: 1570px;
            }
        }
    
        .c-content-panel .c-body {
            padding: 20px;
        }

        .krh-devblog-list-title a {
            font-size: 24px !important;
            font-weight: 500;
        }

        .krh-devblog p {
            color: #5c6873;
            font-weight: 300;
            margin-top: 10px;
        }

        .krh-devblog-filter {
            font-size: 14px;
            float: right;
            display: inline-block;
        }

        .krh-devblog-filter ul {
            display: inline-block;
            -webkit-padding-start: 0;
        }

        .krh-devblog-filter li {
            font-size: 13px;
            text-transform: uppercase;
            display: inline-block;
            list-style: none;
            padding: 2px 6px 1px 6px;
            margin: 0 2px;
        }

        .krh-border-theme {
            border-color: #5893dd;
            border-style: dashed;
        }
        
        .krh-blog-img {
            border: dashed #aabbaa 1px;
        }

        .c-layout-breadcrumbs-1 {
            padding: 0;
            margin-bottom: 40px;
        }

        .krh-devblog .c-panel .c-tags > li > a {
            color: white;
            font-weight: 400;
        }
		
		#disqus_thread {
			min-height: 100px;
		}
		

	</style>
{% endsection %}

{% section scripts %}
    <script>
        window.selectedMenuItems = ["c-menu-item-products", "c-menu-item-blog"];
    </script>
    
	<script async src="https://static.addtoany.com/menu/page.js"></script>
    
	<script defer>
		var disqus_config = function () {
			this.page.url = window.location.href; 
			this.page.identifier = "{{ disqus_id | default: page.id }}"; // Replace PAGE_IDENTIFIER with your page's unique identifier variable
		};
		var disqus_observer = new IntersectionObserver(function(entries) {
			// comments section reached
			// start loading Disqus now
			if(entries[0].isIntersecting) {			/**
				*  RECOMMENDED CONFIGURATION VARIABLES: EDIT AND UNCOMMENT THE SECTION BELOW TO INSERT DYNAMIC VALUES FROM YOUR PLATFORM OR CMS.
				*  LEARN WHY DEFINING THESE VARIABLES IS IMPORTANT: https://disqus.com/admin/universalcode/#configuration-variables*/
				(function () { // DON'T EDIT BELOW THIS LINE
					var d = document, s = d.createElement('script');
					s.src = 'https://net-tricks-blog.disqus.com/embed.js';
					s.setAttribute('data-timestamp', +new Date());
					(d.head || d.body).appendChild(s);
				})();
				
				// once executed, stop observing
				disqus_observer.disconnect();
			}
		}, { threshold: [0] });
		disqus_observer.observe(document.querySelector("#disqus_thread"));
	</script>
	
    <script type="text/javascript" src="/js/slider-init.js"></script>

{% endsection %}

