@using Gazda.Models.Content
@using Gazda.Web
@using Microsoft.AspNetCore.Html
@using Microsoft.AspNetCore.WebUtilities
@using Microsoft.AspNetCore.Http.Extensions

@{
    Layout = "/__GazdaCms/__GazdaLayout/Product";
    var Blog = Context.Items["__GazdaBlog"] as Gazda.Models.Content.Blog;
    IgnoreBody();
    ViewData["Title"] = Blog.Title;

    var BlogPosts = Blog.Posts;
    var postsPerPage = 5;
    var pageCount = BlogPosts.Count() == 0 ? 1 : (BlogPosts.Count() + (postsPerPage - 1)) / postsPerPage;

    var page = 1;
    if (Blog.IsFiltered && Blog.Filter.ContainsKey("page")) {
        page = Int32.Parse(Blog.Filter["page"]);
    }

}

@functions {
    public static String GetPageLink(Microsoft.AspNetCore.Http.HttpContext context, Blog blog, int page)
    {
        var url = blog.Path;

        var query = QueryHelpers.ParseQuery(context.Request.QueryString.ToString());
        var items = query.SelectMany(x => x.Value, (col, value) => new KeyValuePair<string, string>(col.Key, value)).ToList();

        items.RemoveAll(x => x.Key == "page");

        var qb = new QueryBuilder(items);
        qb.Add("page", page.ToString());

        return url + qb.ToQueryString();
    }
}


<div class="c-content-box c-size-sm c-overflow-hide c-bg-white">
    <div class="container krh-devblog">
        <div class="c-layout-breadcrumbs-1 c-fonts-uppercase c-fonts-bold c-bordered c-bordered-both">
            <div class="container" style="margin-left:0;">
                <ul class="c-page-breadcrumbs c-theme-nav c-pull-left c-fonts-regular">
                    <li>
                        Blogs
                    </li>
                    <li>/</li>
                    <li class="c-state_active">
                        <a href="@Blog.Path">@Blog.Title</a>
                    </li>
                </ul>
            </div>
        </div>
        <div class="row">
            <div class="col-lg-8">
                <div class="c-content-blog-post-1-list">
                    <div class="c-content-title-1 c-margin-b-50">
                        <h1>@Blog.Title</h1>
                        @if (Blog.IsFiltered && @Blog.Filter.ContainsKey("tag"))
                        {
                            <div class="krh-devblog-filter">
                                Filtered by: <ul class="c-tags c-theme-ul-bg"><li>@Blog.Filter["tag"]</li></ul>
                            </div>
                        }
                        <div class="c-line-left c-theme-bg"></div>
                    </div>
                    @if (Blog.IndexContent == "" && BlogPosts != null)
                    {
                        @foreach (var post in BlogPosts.OrderByDescending(p => p.DatePublished).Skip((page - 1) * postsPerPage).Take(postsPerPage))
                        {
                        <div class="c-content-blog-post-1">
                            <div class="c-title krh-devblog-list-title">
                                <a href="@Blog.GetPostPath(post)">@post.Title</a>
                            </div>
                            <div class="c-desc">
                                <p>@post.Excerpt</p>
                                <a href="@Blog.GetPostPath(post)">read more...</a>
                            </div>
                            <div class="c-panel">
                                <div class="c-date">@post.DatePublishedStr</div>
				                @if (!String.IsNullOrEmpty(post.Tags))
                                {
                                    String[] tags = post.Tags.Split(',');
                                    <ul class="c-tags c-theme-ul-bg">
                                        @foreach (var tag in tags)
                                        {
                                            <li><a href="@Blog.Path?tag=@tag">@tag</a></li>
                                        }
                                    </ul>
                                }
                            </div>
                        </div>
                        }


                        @if (pageCount > 1)
                        {
                            <div class="c-pagination">
                                <ul class="c-content-pagination c-theme">
                                    @if (page != 1)
                                    {
                                        <li class="c-prev"><a href="@GetPageLink(Context, Blog, page-1)"><i class="fa fa-angle-left"></i></a></li>
                                    }

                                    @for (var i = 1; i <= pageCount; i++)
                                    {
                                        @if (page == i)
                                        {
                                        <li class="c-active"><a href="@GetPageLink(Context, Blog, i)">@i</a></li>
                                        }
                                        else
                                        {
                                        <li><a href="@GetPageLink(Context, Blog, i)">@i</a></li>
                                        }
                                    }
                                    @if (page != pageCount)
                                    {
                                        <li class="c-next"><a href="@GetPageLink(Context, Blog, page+1)"><i class="fa fa-angle-right"></i></a></li>
                                    }
                                </ul>
                            </div>
                        }
                    }                    
                    else
                    {
                        @RenderBody()
                    }
                </div>
            </div>
            <div class="col-lg-4">
                <div class="c-content-panel krh-border-theme">
                    <div class="c-body krh-blog-post-list">
                        <p>@Blog.Description</p>
                    </div>
                </div>
                <div class="c-content-title-1 c-theme c-title-md c-margin-t-80">
                    <h3 class="c-font-bold c-font-uppercase">Supported by EasyQuery</h3>
                    <div class="c-line-left c-theme-bg"></div>
                </div>
                <div class="c-margin-b-40">
                    <a href="/easyquery">EasyQuery</a> is a multi-platform solution that lets you embedding ad-hoc reporting or advanced search functionality to your application
                </div>
                <div class="c-tile-content c-arrow-right c-arrow-green c-content-overlay">
                    <a href="//korzh.com/easyquery">
                        <div class="c-overlay-wrapper" style="width: 300px;">
                            <div class="c-overlay-content">
                                <i class="icon-link"></i>
                            </div>
                        </div>
                    </a>
                    <div class="c-image c-overlay-object" data-height="height" style="background-image: url(&quot;//korzh.com/static/easyquery/banners/eq_demo_box.gif&quot;); height: 251px; width: 300px;"></div>
                </div>
            </div>
        </div>
    </div>
</div>


