<div class="c-content-box c-size-sm c-overflow-hide c-bg-white">
    <div class="c-layout-sidebar-menu c-theme">
        @Html.Partial("/__GazdaCms/__partial/DocsMenu")
    </div>
    <div class="c-layout-sidebar-content ">
        <div class="c-layout-breadcrumbs-1 c-fonts-uppercase c-fonts-bold c-bordered c-bordered-both">
            @if(User.IsInRole("BLOGGER") || User.IsInRole("CONTENT")) {
                <div class="pull-right" style="margin-top:6px">
                    <a href="/admin/blog/postedit/@Post.Id" class="btn btn-xs btn-default" target="_blank">Edit post</a>
                </div>
            }
            <div class="container" style="margin-left:0;">
                <ul class="c-page-breadcrumbs c-theme-nav c-pull-left c-fonts-regular">
                    <li>
                        <a href="~/easyquery/docs">EasyQuery Documentation</a>
                    </li>
                    <li>/</li>
                    <li>
                        <a href="@BlogPost.Blog.Path">@BlogPost.Blog.Title</a>
                    </li>
                    <li>/</li>
                    @if(!String.IsNullOrEmpty(BlogPost.ParentPostId)) {
                        var parentPost = BlogPost.Blog.Posts.Where(p => p.Id == BlogPost.ParentPostId).First();
                        <li>
    						<a href="@BlogPost.Blog.GetPostPath(parentPost)">@parentPost.Title</a>
                        </li>
                        <li>/</li>
                    }
                    <li class="c-state_active">@BlogPost.Title</li>
                </ul>

            </div>
        </div>
        <!-- BEGIN: PAGE CONTENT -->
        <div class="c-content-blog-post-1 krh-blogpost">
            <div class="c-content-title-1">
                <h1>@BlogPost.Title</h1>
            </div>
            @RenderBody()
            <hr>
            <div class="c-panel">
				@if (!String.IsNullOrEmpty(BlogPost.Tags)) {
					String[] tags = BlogPost.Tags.Split(','); 
					<h5 style="display: inline-block; float:left">Tags:&nbsp;</h5>
					<ul class="c-tags c-theme-ul-bg">
						@foreach (var tag in tags)
						{
							<li><a href="@BlogPost.Blog.Path?tag=@tag">@tag</a></li>
						}
					</ul>
				}
                <h5 class="c-right">
                    @if (BlogPost.DatePublished != null) {
                        <text>Published:</text> <span class="c-font-bold">@BlogPost.DatePublishedStr</span>;  
                    }
                    else {
                        <span class="label label-danger">Not published</span>
                    }
                     Last updated: <span class="c-font-bold">@BlogPost.DateUpdated.ToString("yyyy-MM-dd")</span>
                </h5>
            </div>
        </div>
    </div>
</div>       

