@using Gazda.Models.Content
@using System.Linq
@inject Gazda.ViewServices.IBlogViewService blogService

@{
    ViewData["Title"] = "EasyQuery Documentation";

    string[] blogList = new string[] {
        "easyquery_docs_getting-started",
        "easyquery_release-notes",
        "easyquery_docs_fundamentals",
        "easyquery_docs_tutorials",
        "easyquery_use-cases",
        "easyquery_docs_how-to",
        "easyquery_docs_javascript"
    };

    var blogs = new List<Blog>();
    foreach (var item in blogList)
    {
        try
        {
            blogs.Add(await blogService.GetBlogByIdAsync(item));
        }
        catch { }
    }
}

<div class="c-content-box c-size-sm c-overflow-hide c-bg-white">
    <div class="c-layout-sidebar-menu c-theme">
        @Html.Partial("/__GazdaCms/__partial/DocsMenu")
    </div>
    <div class="c-layout-sidebar-content ">
        <div class="c-content-blog-post-1 krh-blogpost">
            <div class="c-content-title-1">
                <h1>EasyQuery Documentation</h1>
                <div class="c-line-center c-theme-bg c-hide"></div>
            </div>

            <h3 class="c-dropdown c-open" id="__default~easyquery_docs_introduction">
                <a href="/easyquery/docs/introduction">Introduction</a>
            </h3>
            @foreach (var blog in blogs)
            {
                <h3 class="c-dropdown c-open" id="@blog.Id">
                    <a href="@blog.Path">@blog.Title</a>
                </h3>
                <ul class="c-content-list-1 c-theme c-separator-dot c-square">
                    @foreach (var post in blog.Posts.OrderByDescending(p => p.DatePublished).OrderBy(p => p.Index).Take(2))
                    {
                        <li id="@post.Id">
                            <a href="@blog.GetPostPath(post)">@post.Title</a>
                        </li>
                    }
                </ul>
                <a href="@blog.Path">More...</a>
            }
            <h3 class="c-dropdown c-open" id="easyquery_docs_end-user-guide">
                <a href="/easyquery/docs/end-user-guide ">End-user's guide</a>
            </h3>

        </div>
    </div>
</div>

@section styles{
    <link href="/static/css/docmenu.css" rel="stylesheet" type="text/css" />
    <style>
        .c-content-blog-post-1 h3 a {
            color: #41515b;
        }

        .c-content-blog-post-1 h3 a:hover {
            color: black;
        }
    </style>
}


@section scripts{
    <script>
        window.selectedMenuItems = ["c-menu-item-eq"];
    </script>
}

