@using System.Collections.Generic
@using Microsoft.AspNetCore.Http
@using Microsoft.AspNetCore.Identity
@using Gazda.Web
@using Gazda.ViewModels.Client

@model Gazda.ViewModels.Client.LoginViewModel
@inject SignInManager<Gazda.Models.Account> SignInManager

@{
    ViewData["Title"] = "Sign-in";
    var Email = Context.Request.Query["Email"];
    
    var intent = Context.Request.Query["intent"].ToString();
    intent = intent != null ? intent : "support";
    var vi = Context.Request.Query["vi"].ToString();
    var ptag = Context.Request.Query["ptag"].ToString();
    ptag = ptag != null ? ptag : "EQN-ANC";
    
    var registerUrl = "/register?src=login";
    if (!string.IsNullOrEmpty(intent)) {
        registerUrl += "&intent=" + intent;
    }

    if (!string.IsNullOrEmpty(ptag)) {
        registerUrl += "&ptag=" + ptag;
    }

    if (!string.IsNullOrEmpty(vi)) {
        registerUrl += "&vi=" + vi;
    }
}

<div class="modal-dialog" style="margin:120px auto">
    <div class="modal-content c-square">
        <div class="modal-body">
            <h3 class="c-font-24 c-font-sbold">@ViewData["Title"]</h3>
            @Html.RenderFlashAlerts()
            <form action="/login" method="post" role="form">
                <input name="ReturnUrl" type="hidden" value="@(Context.Request.Query["ReturnUrl"])" />
                <input type="hidden" name="vi" value="@vi" />
                <input type="hidden" name="intent" value="@intent" />
                <input type="hidden" name="ptag" value="@ptag" />                
                <div class="form-group">
                    <label class="hide" for="login-email">Email</label>
                    <input type="email" placeholder="Email" id="login-email" name="Email" value="@Email" class="form-control input-lg c-square">
                    <span asp-validation-for="Email" class="text-danger"></span>
                </div>
                <div class="form-group">
                    <label class="hide" for="login-password">Password</label>
                    <input type="password" placeholder="Password" id="login-password" name="Password" class="form-control input-lg c-square">
                    <span asp-validation-for="Password" class="text-danger"></span>
                </div>
                <div class="form-group">
                    <div class="c-checkbox">
                        <input type="checkbox" class="c-check" id="login-dontrememberme" name="DontRememberMe">
                        <label class="c-font-thin c-font-17" for="login-dontrememberme">
                            <span></span>
                            <span class="check"></span>
                            <span class="box"></span> Not my computer (don't remember me here)
                        </label>
                    </div>
                </div>
                <div class="form-group">
                    <p class="c-font-14 c-margin-t-30">* By clicking Login, you agree to our <a href="~/terms-of-use">Terms of use</a> and our <a href="~/privacy">Privacy Policy</a></p>
                </div>
                <div class="form-group">
                    <button class="btn c-theme-btn btn-md c-btn-uppercase c-btn-bold c-btn-square c-btn-login c-margin-r-20" type="submit">Login</button>
                    <a href="~/account/auth/forgotpassword" class="c-btn-forgot">Forgot Your Password?</a>
                </div>
