    var ORDER_STATUS = Context.Request.Form["ORDER_STATUS"];
    var ORDER_STATUS_ID = Context.Request.Form["ORDER_STATUS_ID"];
    var PAYMENT_METHOD_ID = Context.Request.Form["PAYMENT_METHOD_ID"];
    var CUSTOMER_NAME = System.Net.WebUtility.UrlDecode(Context.Request.Form["CUSTOMER_NAME"]);
    var INVOICE_LINK = System.Net.WebUtility.UrlDecode(Context.Request.Form["INVOICE_LINK"]);
    var IS_DELAYED_PAYMENT = Context.Request.Form["IS_DELAYED_PAYMENT"];
    var ORDER_ITEMS_COUNT = Int32.Parse(Context.Request.Form["ORDER_ITEMS_COUNT"]);
	
	string[] orderProductIds = new string[ORDER_ITEMS_COUNT];
	string[] orderProductNames = new string[ORDER_ITEMS_COUNT];
	string[] orderProductCount = new string[ORDER_ITEMS_COUNT];
	
	for(int i = 0; i < ORDER_ITEMS_COUNT; i++) {
		orderProductIds[i] = Context.Request.Form[$"ORDER_ITEMS[{i}][PRODUCT_ID]"];
		orderProductNames[i] = System.Net.WebUtility.UrlDecode(Context.Request.Form[$"ORDER_ITEMS[{i}][ORDER_ITEM_NAME]"]);
		orderProductCount[i] = Context.Request.Form[$"ORDER_ITEMS[{i}][PRODUCT_QUANTITY]"];
	}
}

