    if (!string.IsNullOrEmpty(email)) {
        var client = await clientService.GetByEmailAsync(email);
        if (client != null) {
            client.Options = client.Options & ~(Account.OPT_RECEIVE_NEWS);
            await clientService.SaveAsync(client);
        }
    } 
      
